import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import Header from '../imports/Header';
import AddUserForm from '../imports/AddUserForm';
import { createAction } from '../../container/actions';
import { useNavigate } from 'react-router-dom';

export default function AddUser() {
    // hook variables
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [gender, setGender] = useState("");
    const [status, setStatus] = useState("");
    const [errorMessage, setError] = useState("");

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        const newUser = {
            name,
            email,
            gender,
            status
        };
        const validate = dispatch(createAction(newUser));
        console.log('ok');
        validate.then(data => {
            navigate('/add-user');
        }).catch(error => {
            setError(error.data.err);
        });
    };
    let addUserData = {
        handleSubmit,
        setName,
        setEmail,
        setGender,
        setStatus,
        errorMessage,
        setError
    };

    return (
        <>
            <Header></Header>
            <main id="site-main">
                <div className="container">
                    <div className="box-nav d-flex-justify-between">
                        <div className="filter">
                            <a href="/" className="fas fa-angle-double-left">All Users</a>
                        </div>
                    </div>
                    <div className="form-title text-center">
                        <h2 className="text-dark">New User</h2>
                        <span className="text-light">Use the below form to create a new account</span>
                    </div>
                    <AddUserForm addUserState={addUserData}></AddUserForm>
                </div>
            </main>
        </>
    )
}
