import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import BaseLogin from '../imports/BaseLogin';
import RegisterForm from '../imports/RegisterForm';
import { registerAction } from '../../container/actions';

export default function Register() {
    // hook variables
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [passwordCheck, setPasswordCheck] = useState("");

    const dispatch = useDispatch();

    // event handler on form submission 
    const handleSubmit = (event) => {
        event.preventDefault(); // prevent defualt behaviour of this element
        const newUser = {
            username,
            email,
            password,
            passwordCheck
        }
        const userPayload = { username: "admin", email: "admin@gmail.com", password: "admin123", passwordCheck: "admin123" };
        const validate = registerAction(userPayload);
        validate.then(data => {
            console.log(data)
        }).catch(error => console.log(error));
        console.log(newUser);
    };

    let registerData = {
        handleSubmit,
        setUsername,
        setEmail,
        setPassword,
        setPasswordCheck
    };

    return (
        <div id="login">
            <div className="container">
                <div className="row login-box">
                    <BaseLogin />
                    <RegisterForm registerState={registerData} />
                </div>
            </div>
        </div>
    );
}
