import React, { useState, useEffect, useSelector } from 'react'
import UpdateForm from '../imports/UpdateForm';
import Header from '../imports/Header';
import { useDispatch } from 'react-redux';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { getUserInfoAction, updateAction } from '../../container/actions';

export default function Update() {

    const navigate = useNavigate();

    const route = () => {
        const token = localStorage.getItem('x-access-token');
        return token ? true : false;
    }

    useEffect(() => {
        if (!route()) {
            navigate('/login');
        }
    }, [route, navigate]);

    const dispatch = useDispatch();

    const [searchParams, setSearchParams] = useSearchParams();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [gender, setGender] = useState("");
    const [status, setStatus] = useState("");
    const [errorMessage, setError] = useState("");
    let id = searchParams.get("id");

    const getData = dispatch(getUserInfoAction(id));
    getData
        .then(data => {
            setName(data.name);
            setEmail(data.email);
            setGender(data.gender);
            setStatus(data.status);
        }).catch(error => {
            setError(error.data.err);
        });

    const handleSubmit = (event) => {
        event.preventDefault();
        const updateUser = {
            name,
            email,
            gender,
            status
        };
        const validate = dispatch(updateAction(updateUser));
        validate.then(data => {
            navigate('/');
        }).catch(error => {
            setError(error.data.err);
        });
    };

    let updateUserData = {
        handleSubmit,
        id,
        name,
        setName,
        email,
        setEmail,
        gender,
        setGender,
        status,
        setStatus,
        errorMessage,
        setError
    };

    return (
        <>
            <Header></Header>
            <main id="site-main">
                <div className="container">
                    <UpdateForm UpdateUserState={updateUserData}></UpdateForm>
                </div>
            </main>
        </>
    )
}
