import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import Error from './Error';

export default function UpdateForm(props) {
    // let { handleSubmit,
    //     id,
    //     name,
    //     setName,
    //     email,
    //     setEmail,
    //     gender,
    //     setGender,
    //     status,
    //     setStatus,
    //     errorMessage,
    //     setError
    // } = props.UpdateUserState;
    let { handleSubmit,
        userData,
        setUserData
    } = props.UpdateUserState;
    return (
        <main id="site-main">
            <div className="container">
                <div className="box-nav d-flex-justify-between">
                    <div className="filter">
                        <Link to="/" className="fas fa-angle-double-left">All Users</Link>
                    </div>
                </div>
                <div className="form-title text-center">
                    <h2 className="text-dark">Update User</h2>
                    <span className="label-light">Use the below form to update an existing account</span>
                </div>

                <form method="POST" id="update_user" onSubmit={handleSubmit}>
                    <div className="new_user">
                        <div className="form-group">
                            <label htmlFor="Name" className="label-light">Name</label>
                            <input type="hidden" name="id" value={userData.id} />
                            <input type="text" name="name" defaultValue={userData.name} onChange={e => setUserData(userData.name = e.target.value)} placeholder="Mark Stoenis" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="Email" className="label-light">Email</label>
                            <input type="text" name="email" onChange={e => setUserData(userData.email = e.target.value)} defaultValue={userData.email} placeholder="example@test.com" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="gender" className="label-light">Gender</label>
                            <div className="radio inline">
                                <input type="radio" id="radio-2" onChange={(e) => setUserData(userData.gender = e.target.value)} name="gender" value="Male" />
                                <label htmlFor="radio-2" className="radio-label">Male</label>
                            </div>
                            <div className="radio inline">
                                <input type="radio" id="radio-3" onChange={(e) => setUserData(userData.gender = e.target.value)} name="gender" value="Female" />
                                <label htmlFor="radio-3" className="radio-label">Female</label>
                            </div>
                        </div>
                        <div className="form-group">
                            <label htmlFor="status" className="label-light">Status</label>
                            <div className="radio inline">
                                <input type="radio" id="radio-4" name="status" value="Active" onChange={e => setUserData(userData.status = e.target.value)} />
                                <label htmlFor="radio-4" className="radio-label">Active</label>
                            </div>
                            <div className="radio inline">
                                <input type="radio" id="radio-5" name="status" value="Inactive" onChange={e => setUserData(userData.status = e.target.value)} />
                                <label htmlFor="radio-5" className="radio-label">Inactive</label>
                            </div>
                        </div>
                        {
                            userData.error && <Error errorMessage={userData.error} clearError={() => { setUserData(userData.error = undefined) }}></Error>
                        }
                        <div className="form-group">
                            <button type="submit" className="btn text-dark update">Save</button>
                        </div>
                    </div>
                </form>
            </div>
        </main>
    )
}
