import React, { useState, useEffect } from 'react'
import UpdateForm from '../imports/UpdateForm';
import Header from '../imports/Header';
import { useDispatch } from 'react-redux';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { getUserInfoAction, updateAction } from '../../container/actions';

export default function Update() {

    const navigate = useNavigate();
    const route = () => {
        const token = localStorage.getItem('x-access-token');
        return token ? true : false;
    }
    useEffect(() => {
        if (!route()) {
            navigate('/login');
        }
    }, [route, navigate]);

    const dispatch = useDispatch();

    const [searchParams, setSearchParams] = useSearchParams();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [gender, setGender] = useState("");
    const [status, setStatus] = useState("");
    const [errorMessage, setError] = useState("");
    const id = searchParams.get("id");
    let tmpUserData = {};

    const getData = dispatch(getUserInfoAction(id));
    getData
        .then(data => {
            tmpUserData = data;
        }).catch(error => {
            setError(error.data.err);
        });

    const handleSubmit = (event) => {
        event.preventDefault();
        const updateUser = {
            name,
            email,
            gender,
            status
        };
        const validate = dispatch(updateAction(updateUser)); // id?
        validate.then(data => {
            navigate('/');
        }).catch(error => {
            setError(error.data.err);
        });
    };

    let states = {
        handleSubmit,
        id,
        setName,
        setEmail,
        setGender,
        setStatus,
        errorMessage,
        setError
    };
}
