import React, { useState, useEffect } from 'react'
import UpdateForm from '../imports/UpdateForm';
import Header from '../imports/Header';
import { useDispatch } from 'react-redux';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { getUserInfoAction } from '../../container/actions';

export default function Update() {
    const navigate = useNavigate();

    const route = () => {
        const token = localStorage.getItem('x-access-token');
        return token ? true : false;
    }

    useEffect(() => {
        if (!route()) {
            navigate('/login');
        }
    }, [route, navigate]);

    const dispatch = useDispatch();

    const [searchParams, setSearchParams] = useSearchParams();

    const [errorMessage, setError] = useState("");
    const [userData, setUserData] = useState({ user: {} });
    const getData = dispatch(getUserInfoAction(searchParams.get("id")));
    getData
        .then(data => {
            setUserData({ user: data });
        }).catch(error => {
            setError(error.data.err);
        });

    return (
        <>
            <Header></Header>
            <main id="site-main">
                <div className="container">
                    <UpdateForm data={[userData.user]}></UpdateForm>
                </div>
            </main>
        </>
    )
}
